INSERT INTO `vacante` (`id_vacante`, `id_aspirante`, `id_empresa`, `empleador`, `titulo`, `descripcion`, `funciones`, `requisitos`, `beneficios`, `notas`, `duracion`, `salario`, `vigencia`, `ubicacion`, `ciudad`, `estado`, `pais`, `telefono`, `correo`, `sitioweb`, `contacto`, `activo`) VALUES
(1, 0, 0, 'TECMIN Drilling & Exploration', 'Supervisor de Proyectos', '<p>EDAD: 25 A 50 A&Ntilde;OS</p>\r\n<p>ESCOLARIDAD: Carrera a Fin (Ing. Industrial, Ge&oacute;logo, Ing. Minas)</p>\r\n<p>SEXO: Masculino&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Manejo&nbsp; de personal</p>\r\n<p>Organizaci&oacute;n, planeaci&oacute;n en actividades en campo</p>\r\n<p>Toma de decisiones</p>\r\n<p>Liderazgo</p>\r\n<p>Observaci&oacute;n</p>\r\n<p>&nbsp;</p>', '<p>a</p>', '<p>En Maquinas para Barre naci&oacute;n con diamante en superficie e interior de mina.</p>\r\n<p>Di&aacute;metros HQ, NQ y BQ.</p>\r\n<p>Manejo de sistemas Wire Line</p>\r\n<p>Conocimientos&nbsp; generales en mec&aacute;nica.</p>\r\n<p>Seguridad y Ecolog&iacute;a</p>\r\n<p>Manejo de Word, Excel e Internet.</p>', '<p>a</p>', '<p>a</p>', NULL, NULL, NULL, 'a', NULL, NULL, '', NULL, NULL, NULL, 'reclutamiento@outletminero.org', 1),
(2, 0, 0, 'Tecmin', 'Perforista', '<p>Manejo de m&aacute;quinas diamantinas, mec&aacute;nicas e hidr&aacute;ulicas en interior o superficie de mina.</p>', '', '<p>Conocimiento en mec&aacute;nica general.</p>\r\n<p>Extracci&oacute;n de n&uacute;cleo, trabajo en equipo, conocimientos en seguridad y ecolog&iacute;a</p>', '', '<p>Que sea responsable, con manejo de estr&eacute;s y sociable.</p>', NULL, NULL, NULL, 'Fresnillo Zac.', NULL, NULL, '', NULL, NULL, NULL, 'www.tecmin.com.mx', 1),
(3, 0, 0, 'Tecmin', 'Ayudante de Perforista', '<p>Conocimiento en m&aacute;quinas diamantinas. Mec&aacute;nicas e Hidr&aacute;ulicas en interior y superficie de mina.</p>', '', '<p>20 a 55 a&ntilde;os, masculino, con experiencia m&iacute;nima de 6 meses como ayudante de perforista, con conocimiento en m&aacute;quinas diamantinas</p>', '', '<p>Que sepa trabajar en equipo, con disposici&oacute;n de aprendizaje, responsable y con manejo de estr&eacute;s.&nbsp;</p>', NULL, NULL, NULL, 'Fresnillo Zac.', NULL, NULL, '', NULL, NULL, NULL, 'www.tecmin.com.mx', 1),
(4, 0, 0, 'Tecmin', '2do. Ayudante de Perforista', '', '', '<p>Conocimientos generales de herramientas. No requerida la experiencia en maquinas diamantinas. Con habilidades de trabajo en equipo, capacidad de aprendizaje, responsabilidad, sociabilidad. Entre 18 y 45 a&ntilde;os. Masculino.&nbsp;</p>', '', '', NULL, NULL, NULL, 'Fresnillo Zac.', NULL, NULL, '', NULL, NULL, NULL, 'www.tecmin.com.mx', 1),
(5, 0, 0, 'Tecmin', 'Chofer', '<p>Manejo de pipas de agua, fresnos de aire, trasmisi&oacute;n con dual.</p>', '', '<p>Edad de 20 a 40 a&ntilde;os, masculino, con experienca en manejo de pipas de agua, fresnos de aire, trasmisi&oacute;n con dual y manejo en terracer&iacute;a y sierra. Con conocimiento en mec&aacute;nica, responsable, din&aacute;mico y con disposici&oacute;n de servicio.</p>', '', '', NULL, NULL, NULL, 'Fresnillo Zac.', NULL, NULL, '', NULL, NULL, NULL, 'www.tecmin.com.mx', 1),
(6, 0, 0, 'Tecmin', 'Tornero', '', '', '<p>Experiencia en manejo de micr&oacute;metros de tacto interior y exterior, compases telesc&oacute;picos, manejo de cepillo y fresadora, con habilidades de uso de herramientas, responsable, entre 35 y 55 a&ntilde;os y con escolaridad Tecnica o media superior.</p>', '', '', NULL, NULL, NULL, 'Fresnillo Zac.', NULL, NULL, '', NULL, NULL, NULL, 'www.tecmin.com.mx', 1),
(7, 0, 0, 'Tecmin', 'Encargado de Seguridad y EcologÃ­a', '', '', '<p>Experiencia en impacto ambiental, operaci&oacute;n de equipo, desarrollo de estudio de riesgo ambiental, manejo de normas, salud y protecci&oacute;n ambiental, disponibilidad de radicar fuera, experiencia m&iacute;nima de 5 a&ntilde;os. Con habilidades en manejo de programas computacionales, manejo de personal, liderazgo y trabajo en equipo.</p>', '', '', NULL, NULL, NULL, 'Fresnillo Zac.', NULL, NULL, '', NULL, NULL, NULL, 'www.tecmin.com.mx', 1);
